import React, { Component } from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';
import './App.css'

class  App extends Component { // smart component since it has state 

    constructor (){
        super()
        this.state ={
            robots: [],
            searchfield:''

        }
    }

    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/users').then(response=>{
            response.json();

        })
        .then(users=>{
            this.setState({robots: users})
        })
        this.setState({robots: robots})
    }

    onSearchChange = (event) => {
        this.setState({searchfield: event.target.value}) // in order to update state upon typing inside searchfiled
        // as search filed in defualt with value '' so we need to update it

    }

    render(){

        const  filteredRobots = this.state.robots.filter(robots=>{
            return robots.name.toLocaleLowerCase().includes(this.state.searchfield.toLocaleLowerCase());
        })
        console.log(filteredRobots );

        return(
            <div className='tc'>
                <h1 className='f1'>RoboFriends</h1>
                <SearchBox searchChange={this.onSearchChange}/>
                <CardList robots={filteredRobots}/>
            </div>
        );
    }
}
export default App;
