import React, { Component } from 'react';
import CardList from '../components/CardList';
import SearchBox from '../components/SearchBox';
import Scroll from '../components/Scroll'
import './App.css'

class  App extends Component { // smart component since it has state 

    constructor (){
        super()
        this.state ={
            robots: [],
            searchfield:''

        }
    }

    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response=>{return response.json();})
            .then(users=>(this.setState({robots: users})));

    }

    onSearchChange = (event) => {
        this.setState({searchfield: event.target.value}) // in order to update state upon typing inside searchfiled
        // as search filed in defualt with value '' so we need to update it

    }

    render() 
    {
        const { robots, searchfield } = this.state;
        const filteredRobots = robots.filter(robot =>{
          return robot.name.toLowerCase().includes(searchfield.toLowerCase());
        })
        return !robots.length ?
          <h1>Loading</h1> :
          (
            <div className='tc'>
                <h1 className='f1'>RoboFriends</h1>
                <SearchBox searchChange={this.onSearchChange}/>
                <Scroll>
                    <CardList robots={filteredRobots}/>
                </Scroll>
            </div>
          );
    }
}
export default App;
